// export default CommonSection
import React, { useState, useEffect } from 'react';
import './common-section.css';

const CommonSection = ({ title }) => {
  const images = [
    "../assets/images/tour.jpg",
    "../assets/images/pexels-pixabay-147411.jpg",
    "../assets/images/pexels-tomas-malik-793526-1998439.jpg",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isFading, setIsFading] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsFading(true);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        setIsFading(false);
      }, 500); // Fade duration matches CSS transition duration
    }, 3000);

    return () => clearInterval(interval);
  }, [images.length]);

  const goToSlide = (index) => {
    setIsFading(true);
    setTimeout(() => {
      setCurrentIndex(index);
      setIsFading(false);
    }, 500);
  };

  return (
    <section className={`common__section slide-${currentIndex} ${isFading ? 'fade-out' : ''}`}>
      <div className="container">
        <h1>{title}</h1>
      </div>
      <div className="dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${currentIndex === index ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
          ></span>
        ))}
      </div>
    </section>
  );
};

export default CommonSection;
