import React, { useContext, useState } from 'react';
import CommonSection from '../shared/CommonSection';
import { Container, Row, Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import { BASE_URL } from '../utils/config';
import useFetch from '../hooks/useFetch'; 
import { AuthContext } from './../context/AuthContext';
import { NavLink } from 'react-router-dom'; 
import '../styles/dashboard.css';

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const userId = user._id;

  console.log(userId);
  const { data: bookings, loading, error, reFetch } = useFetch(`${BASE_URL}/booking/user/${userId}`);
  console.log(bookings);

  // Format date
  const options = { day: "numeric", month: "long", year: "numeric" };

  // State for editing booking
  const [editBookingId, setEditBookingId] = useState(null);
  const [updatedBooking, setUpdatedBooking] = useState({
    fullName: '',
    guestSize: '',
    phone: ''
  });

  // Delete Booking function
  const deleteBooking = async (bookingId) => {
    try {
      const response = await fetch(`${BASE_URL}/booking/${bookingId}`, {
        method: 'DELETE',
      });

      const data = await response.json();

      if (data.success) {
        alert('Booking deleted successfully');
        reFetch(); // Refresh the bookings list
      } else {
        alert('Failed to delete the booking');
      }
    } catch (err) {
      console.error(err);
      alert('Error deleting booking');
    }
  };

  // Handle editing booking
  const handleEditClick = (bookingId, booking) => {
    setEditBookingId(bookingId);
    setUpdatedBooking({
      fullName: booking.fullName,
      guestSize: booking.guestSize,
      phone: booking.phone
    });
  };

  // Handle booking update
  const handleUpdateBooking = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${BASE_URL}/booking/${editBookingId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedBooking),
      });
      const data = await response.json();

      if (data.success) {
        alert('Booking updated successfully');
        setEditBookingId(null); // Hide the edit form
        reFetch(); // Refresh the bookings list
      } else {
        alert('Failed to update booking');
      }
    } catch (err) {
      console.error(err);
      alert('Error updating booking');
    }
  };

  return (
    <>
      <CommonSection title="Dashboard" />
      <section>
        <Container>
          <h2 className="text-center pt-5">My Bookings</h2>
          {loading && <h4 className="text-center pt-5">Loading...</h4>}
          {error && <h4 className="text-center pt-5 text-danger">{error}</h4>}
          {!loading && !error && bookings?.length === 0 && (
            <h4 className="text-center pt-5">No bookings found.</h4>
          )}
          {!loading && !error && bookings?.length > 0 && (
            <Row>
              {bookings.map((booking) => (
                <Col lg="6" md="6" sm="12" key={booking._id} className="mb-4">
                  <div className="booking-card">
                    <div className="booking-details">
                      <div className="booking-info">
                        <h5 className="tour__title">
                          <NavLink
                            to={`/tours/${booking.tourId}`}
                            className={(navClass) =>
                              navClass.isActive ? 'active__link' : ''
                            }
                            style={{ textDecoration: 'none' }}
                          >
                            {booking.tourName}
                          </NavLink>
                        </h5>
                        <p className="tour__location">
                          <i className="ri-user-line"></i> {booking.fullName}
                        </p>
                        <p className="tour__rating">
                          <i className="ri-group-line"></i> {booking.guestSize} Guests
                        </p>
                        <p className="tour__phone">
                          <i className="ri-phone-line"></i> {`Phone: ${booking.phone}`}
                        </p>
                        <span className="booking-date">
                          Booked for: {new Date(booking.bookAt).toLocaleDateString('en-IN', options)}
                        </span>
                      </div>
                    </div>
                    <div className="booking-image">
                      <img
                        src={booking.photo || 'https://via.placeholder.com/300x200'}
                        alt={booking.tourName}
                      />
                    </div>
                    <div className="booking-actions">
                      {/* Edit Button */}
                      <Button
                        color="primary"
                        onClick={() => handleEditClick(booking._id, booking)}
                        className="btn-sm"
                      >
                        Edit
                      </Button>
                      {/* Delete Button */}
                      <Button
                        color="danger"
                        onClick={() => deleteBooking(booking._id)}
                        className="btn-sm ml-2"
                      >
                        Delete
                      </Button>
                    </div>
                  </div>

                  {/* Edit Form */}
                  {editBookingId === booking._id && (
                    <div className="edit-form mt-3">
                      <Form onSubmit={handleUpdateBooking}>
                        <FormGroup>
                          <Label for="fullName">Full Name</Label>
                          <Input
                            type="text"
                            name="fullName"
                            id="fullName"
                            value={updatedBooking.fullName}
                            onChange={(e) => setUpdatedBooking({ ...updatedBooking, fullName: e.target.value })}
                          />
                        </FormGroup>
                        <FormGroup>
                          <Label for="guestSize">Guest Size</Label>
                          <Input
                            type="number"
                            name="guestSize"
                            id="guestSize"
                            value={updatedBooking.guestSize}
                            onChange={(e) => setUpdatedBooking({ ...updatedBooking, guestSize: e.target.value })}
                          />
                        </FormGroup>
                        <FormGroup>
                          <Label for="phone">Phone</Label>
                          <Input
                            type="text"
                            name="phone"
                            id="phone"
                            value={updatedBooking.phone}
                            onChange={(e) => setUpdatedBooking({ ...updatedBooking, phone: e.target.value })}
                          />
                        </FormGroup>
                        <Button color="success" type="submit">
                          Save Changes
                        </Button>
                      </Form>
                    </div>
                  )}

                  <hr className="booking-divider" />
                </Col>
              ))}
            </Row>
          )}
        </Container>
      </section>
    </>
  );
};

export default Dashboard;
