import React, { useRef, useEffect, useContext, useState } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link, useNavigate } from 'react-router-dom';

import logo from '../../assets/images/logo-top.png';
import './header.css';

import { AuthContext } from './../../context/AuthContext';
import { FaSun, FaMoon } from 'react-icons/fa'; // Icons for theme toggle

const nav__links = [
  {
    path: '/home',
    display: 'Home',
  },
  {
    path: '/about',
    display: 'About',
  },
  {
    path: '/tours',
    display: 'Tours',
  },
  {
    path: '/gallery',
    display: 'Gallery',
  },
];

const Header = () => {
  const headerRef = useRef(null);
  const menuRef = useRef(null);
  const navigate = useNavigate();
  const { user, dispatch } = useContext(AuthContext);
  const [theme, setTheme] = useState('light');

  const logout = () => {
    dispatch({ type: 'LOGOUT' });
    navigate('/');
  };

  const stickyHeaderFunc = () => {
    window.addEventListener('scroll', () => {
      if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        headerRef.current.classList.add('sticky__header');
      } else {
        headerRef.current.classList.remove('sticky__header');
      }
    });
  };

  useEffect(() => {
    stickyHeaderFunc();

    return () => {
      window.removeEventListener('scroll', stickyHeaderFunc);
    };
  });

  const toggleMenu = () => menuRef.current.classList.toggle('show__menu');

  // Toggle Theme
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    document.body.setAttribute('data-theme', newTheme);
  };

  return (
    <header className="header" ref={headerRef}>
      <Container>
        <Row>
          <div className="nav__wrapper d-flex align-items-center justify-content-between">
            {/* ----------- logo ----------- */}
            <div className="logo">
              <img src={logo} alt="" />
            </div>
            {/* ----------- logo end ----------- */}

            {/* ----------- menu start ----------- */}
            <div className="navigation" ref={menuRef} onClick={toggleMenu}>
              <ul className="menu d-flex align-items-center gap-5">
                {nav__links.map((item, index) => (
                  <li className="nav__item" key={index}>
                    <NavLink
                      to={item.path}
                      className={(navClass) => (navClass.isActive ? 'active__link' : '')}
                      style={{ textDecoration: 'none', marginRight: '40px' }}
                    >
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>
            {/* ----------- menu end ----------- */}
            <div className="nav__right d-flex align-items-center gap-4">
              <div className="nav__item d-flex align-items-center gap-4">
                {user ? (
                  <>
                    <NavLink
                      to={'/dashboard'}
                      className={(navClass) => (navClass.isActive ? 'active__link' : '')}
                      style={{ textDecoration: 'none' }}
                    >
                      {user.username}
                    </NavLink>
                    <Button className="btn btn-dark" onClick={logout}>
                      Logout
                    </Button>
                  </>
                ) : (
                  <>
                    <Button className="btn secondary__btn button-background">
                      <Link to="/login">Login</Link>
                    </Button>
                    <Button className="btn primary__btn button-background">
                      <Link to="/register">Register</Link>
                    </Button>
                  </>
                )}
              </div>

              <span className="mobile__menu" onClick={toggleMenu}>
                <i className="ri-menu-line"></i>
              </span>
            </div>

            {/* Theme Toggle */}
            <button
              className="theme-toggle-btn"
              onClick={toggleTheme}
              style={{
                background: 'transparent',
                border: 'none',
                cursor: 'pointer',
                fontSize: '1.5rem',
                color: 'var(--text-color)',
              }}
              aria-label="Toggle Theme"
            >
              {theme === 'light' ? <FaMoon /> : <FaSun />}
            </button>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
