import React, { useState, useEffect } from 'react';
import './common-section.css';

const CommonSection = ({ title }) => {
  const images = [
    "../assets/images/tour.jpg",
    "../assets/images/pexels-pixabay-147411.jpg",
    "../assets/images/pexels-tomas-malik-793526-1998439.jpg",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [prevIndex, setPrevIndex] = useState(0);

  // Automatically change slides every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setPrevIndex(currentIndex);
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [currentIndex, images.length]);

  // Go to a specific slide
  const goToSlide = (index) => {
    setPrevIndex(currentIndex);
    setCurrentIndex(index);
  };

  return (
    <section className="common__section">
      <div
        className={`image-layer ${currentIndex === prevIndex ? 'fade-in' : 'fade-out'}`}
        style={{ backgroundImage: `url(${images[prevIndex]})` }}
      ></div>
      <div
        className={`image-layer fade-in`}
        style={{ backgroundImage: `url(${images[currentIndex]})` }}
      ></div>
      <div className="container">
        <h1>{title}</h1>
      </div>
      <div className="dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${currentIndex === index ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
          ></span>
        ))}
      </div>
    </section>
  );
};

export default CommonSection;
