import React, { useEffect, useState } from 'react';
import CommonSection from '../shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import { BASE_URL } from '../utils/config';
import '../styles/tour-card.css'; // Reuse the tour card styles

const Dashboard = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const userId = localStorage.getItem('userId'); // Replace with appropriate user ID retrieval

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/bookings/user/${userId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch bookings");
        }
        const data = await response.json();
        setBookings(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, [userId]);

  return (
    <>
      <CommonSection title="My Bookings" />
      <section>
        <Container>
          {loading && <h4 className="text-center pt-5">Loading...</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && bookings.length === 0 && (
            <h4 className="text-center pt-5">No bookings found.</h4>
          )}
          {!loading && !error && bookings.length > 0 && (
            <Row>
              {bookings.map((booking) => (
                <Col lg="4" md="6" sm="12" key={booking._id} className="mb-4">
                  <div className="card">
                    <div className="tour__img">
                      {/* Placeholder image, replace with actual tour image if available */}
                      <img
                        src="https://via.placeholder.com/300x200"
                        alt={booking.tourName}
                      />
                      <span>{new Date(booking.bookAt).toLocaleDateString()}</span>
                    </div>
                    <div className="card__bottom">
                      <h5 className="tour__title">{booking.tourName}</h5>
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <p className="tour__location">
                            <i className="ri-user-line"></i> {booking.fullName}
                          </p>
                          <p className="tour__rating">
                            <i className="ri-group-line"></i> {booking.guestSize} Guests
                          </p>
                        </div>
                        <button className="booking__btn">
                          <a href="#">{`Phone: ${booking.phone}`}</a>
                        </button>
                      </div>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          )}
        </Container>
      </section>
    </>
  );
};

export default Dashboard;
