import React from 'react';
import CommonSection from '../shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import { BASE_URL } from '../utils/config';
import useFetch from '../hooks/useFetch'; // Import the custom hook
import '../styles/tour.css'; // Reuse the tour card styles

const Dashboard = () => {
  const userId = localStorage.getItem('userId'); // Replace with appropriate user ID retrieval
  const { data: bookings, loading, error } = useFetch(`${BASE_URL}/bookings/user/${userId}`);

  return (
    <>
      <CommonSection title="My Bookings" />
      <section>
        <Container>
          {loading && <h4 className="text-center pt-5">Loading...</h4>}
          {error && <h4 className="text-center pt-5 text-danger">{error}</h4>}
          {!loading && !error && bookings?.length === 0 && (
            <h4 className="text-center pt-5">No bookings found.</h4>
          )}
          {!loading && !error && bookings?.length > 0 && (
            <Row>
              {bookings.map((booking) => (
                <Col lg="4" md="6" sm="12" key={booking._id} className="mb-4">
                  <div className="card">
                    <div className="tour__img">
                      {/* Placeholder image, replace with actual tour image if available */}
                      <img
                        src={booking.image || 'https://via.placeholder.com/300x200'}
                        alt={booking.tourName || 'Tour'}
                      />
                      <span>
                        {new Date(booking.bookAt).toLocaleDateString()}
                      </span>
                    </div>
                    <div className="card__bottom">
                      <h5 className="tour__title">
                        {booking.tourName || 'Tour Name Unavailable'}
                      </h5>
                      <div className="d-flex justify-content-between align-items-center">
                        <div>
                          <p className="tour__location">
                            <i className="ri-user-line"></i> {booking.fullName}
                          </p>
                          <p className="tour__rating">
                            <i className="ri-group-line"></i> {booking.guestSize} Guests
                          </p>
                        </div>
                        <button className="booking__btn">
                          <a
                            href={`tel:${booking.phone}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            {`Phone: ${booking.phone || 'N/A'}`}
                          </a>
                        </button>
                      </div>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          )}
        </Container>
      </section>
    </>
  );
};

export default Dashboard;
